mapper.select:
SELECT id, password, name, age 
FROM member
WHERE id = #{id}

SELECT id, password, name, age 
FROM member
WHERE age BETWEEN #{begin} AND #{end}

SELECT id, password, name, age 
FROM member
WHERE name LIKE #{value}

SELECT id, password, name, age FROM member
and id = #{id}
and name LIKE '%' || #{value} || '%'

SELECT id, password, name, age 
FROM member


		or id = #{id}


		or name LIKE '%' || #{name} || '%'


		or age = #{age}

mapper.update:
UPDATE member
SET age = #{age}
WHERE id = #{id}
